import React from 'react';
import './SearchBar.css'



class SearchBar extends React.Component {

    constructor(props){
        super(props)
        //Setting up the initial state of the search bar. Setting up the obj. state: temr & location are set up to empty strings.
        this.state = {
            term : '',
            location : '',
            sortBy : 'best_match'
        };
        this.sortByOptions = {
            'Best Match' : 'best_match',
            'Highest Rated' : 'rating',
            'Most Reviewed' : 'review_count'
        }
        //Binding the methods for the event listeners.
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleLocationChange = this.handleLocationChange.bind(this);
    }

    getSortByClass(sortByOption){
        if(this.state.sortBy === sortByOption){
            return('Active')

        }else{
            return('');
        }
    }

    handleSortByChange(sortByOption){
        this.setState = {
            sortBy : sortByOption
        }

    }

    handleTermChange(event){
        this.setState = {
            term : event.target.value 
        }

    }

    handleLocationChange(event){
        this.setState = {
            location : event.target.value
        }


    }

    renderSortByOptions(){
        return(Object.keys(this.sortByOptions)).map(sortByOption => {

            let sortByOptionValue = this.sortByOptions[this.sortByOptions];

            return(<li key={sortByOptionValue} onClick={this.handleSortByChange.bind(this,sortByOptionValue)} className={this.getSortByClass(sortByOptionValue)}>{sortByOption}</li>)

        });
    }
    //Now create the structure fo my SearchBar Component.
    render(){
        return(
            <div className="SearchBar">
            <div className="SearchBar-sort-options">
                <ul>
                {this.renderSortByOptions()}
                </ul>
            </div>
            <div className="SearchBar-fields">
                <input placeholder="Search Businesses" />
                <input placeholder="Where?" />
            </div>
            <div className="SearchBar-submit">
                <a>Let's Go</a>
            </div>
            </div>
        )
    }
}



export default SearchBar;
